schema:
type Post {
  id: ID!
  title: String!
  createdAt: AWSDateTime
  updatedAt: AWSDateTime
  metadata: PostMetadata
  entityMetadata: EntityMetadata
  appearsIn: [Episode!]
}

type Author {
  id: ID!
  name: String!
  postMetadata: PostMetadata
  entityMetadata: EntityMetadata
}

type EntityMetadata {
  isActive: Boolean
}

type PostMetadata {
  tags: Tag
}

type Tag {
  published: Boolean
  metadata: PostMetadata
}

enum Episode {
  NEWHOPE
  EMPIRE
  JEDI
}

input PostMetadataInput {
  tags: TagInput
}

input TagInput {
  published: Boolean
  metadata: PostMetadataInput
}

input EntityMetadataInput {
  isActive: Boolean
}

enum ModelSortDirection {
  ASC
  DESC
}

type ModelPostConnection {
  items: [Post]
  nextToken: String
}

input ModelStringFilterInput {
  ne: String
  eq: String
  le: String
  lt: String
  ge: String
  gt: String
  contains: String
  notContains: String
  between: [String]
  beginsWith: String
}

input ModelIDFilterInput {
  ne: ID
  eq: ID
  le: ID
  lt: ID
  ge: ID
  gt: ID
  contains: ID
  notContains: ID
  between: [ID]
  beginsWith: ID
}

input ModelIntFilterInput {
  ne: Int
  eq: Int
  le: Int
  lt: Int
  ge: Int
  gt: Int
  contains: Int
  notContains: Int
  between: [Int]
}

input ModelFloatFilterInput {
  ne: Float
  eq: Float
  le: Float
  lt: Float
  ge: Float
  gt: Float
  contains: Float
  notContains: Float
  between: [Float]
}

input ModelBooleanFilterInput {
  ne: Boolean
  eq: Boolean
}

input ModelPostFilterInput {
  id: ModelIDFilterInput
  title: ModelStringFilterInput
  createdAt: ModelStringFilterInput
  updatedAt: ModelStringFilterInput
  and: [ModelPostFilterInput]
  or: [ModelPostFilterInput]
  not: ModelPostFilterInput
}

type Query {
  getPost(id: ID!): Post
  listPosts(filter: ModelPostFilterInput, limit: Int, nextToken: String): ModelPostConnection
  getAuthor(id: ID!): Author
  listAuthors(filter: ModelAuthorFilterInput, limit: Int, nextToken: String): ModelAuthorConnection
}

input CreatePostInput {
  id: ID
  title: String!
  createdAt: AWSDateTime
  updatedAt: AWSDateTime
  metadata: PostMetadataInput
  entityMetadata: EntityMetadataInput
  appearsIn: [Episode!]
}

input UpdatePostInput {
  id: ID!
  title: String
  createdAt: AWSDateTime
  updatedAt: AWSDateTime
  metadata: PostMetadataInput
  entityMetadata: EntityMetadataInput
  appearsIn: [Episode!]
}

input DeletePostInput {
  id: ID
}

type Mutation {
  createPost(input: CreatePostInput!): Post
  updatePost(input: UpdatePostInput!): Post
  deletePost(input: DeletePostInput!): Post
  createAuthor(input: CreateAuthorInput!): Author
  updateAuthor(input: UpdateAuthorInput!): Author
  deleteAuthor(input: DeleteAuthorInput!): Author
}

type Subscription {
  onCreatePost: Post @aws_subscribe(mutations: ["createPost"])
  onUpdatePost: Post @aws_subscribe(mutations: ["updatePost"])
  onDeletePost: Post @aws_subscribe(mutations: ["deletePost"])
  onCreateAuthor: Author @aws_subscribe(mutations: ["createAuthor"])
  onUpdateAuthor: Author @aws_subscribe(mutations: ["updateAuthor"])
  onDeleteAuthor: Author @aws_subscribe(mutations: ["deleteAuthor"])
}

type ModelAuthorConnection {
  items: [Author]
  nextToken: String
}

input ModelAuthorFilterInput {
  id: ModelIDFilterInput
  name: ModelStringFilterInput
  and: [ModelAuthorFilterInput]
  or: [ModelAuthorFilterInput]
  not: ModelAuthorFilterInput
}

input CreateAuthorInput {
  id: ID
  name: String!
  postMetadata: PostMetadataInput
  entityMetadata: EntityMetadataInput
}

input UpdateAuthorInput {
  id: ID!
  name: String
  postMetadata: PostMetadataInput
  entityMetadata: EntityMetadataInput
}

input DeleteAuthorInput {
  id: ID
}
